              {/* Quick AI Actions */}
              <div className="mt-4">
                <h4 className="text-sm font-medium text-muted-foreground mb-2">Quick Generate</h4>
                <div className="grid grid-cols-2 gap-2">
                  <Button 
                    variant="outline" 
                    size="sm" 
