                    <li>Update your graphics drivers</li>
                  </ul>
                </div>
              </CardContent>
            </Card>
          </div>
        ) : (
          <div 
            ref={mountRef} 
            className={`w-full h-full bg-gray-100 ${isDraggingObject ? 'cursor-move' : isRotatingObject ? 'cursor-crosshair' : 'cursor-default'}`}
